"use strict";

const Homey = require('homey');
const Tahoma = require('../../lib/Tahoma');

class Driver extends Homey.Driver {

	onPair(socket) {
		var _this = this;

		socket
			.on('list_devices', (data, callback) => {
				if (_this.deviceType && _this.deviceType instanceof Array && _this.deviceType.length > 0) {
					Tahoma.setup(function(err, data) {
						if (err) {
							return callback(err);
						}
						if (data && data.devices) {
							var devices = new Array();
							for (var i=0; i<data.devices.length; i++) {
								var device = data.devices[i];
								if (_this.deviceType.indexOf(device.controllableName) !== -1) {
									var device_data = {
										name: device.label,
										data: {
											id: device.oid,
											deviceURL: device.deviceURL,
											label: device.label
										}
									};
									devices.push(device_data);
								}
							}

							callback(null, devices);
						}
					});
				} else {
					callback(new Error('missing deviceType'));
				}
			})
			.on('disconnect', () => {
				this.log("User aborted pairing, or pairing is finished");
			});
	}

	triggerFlow(trigger, device, tokens, state) {
		if (trigger) {
			trigger
				.trigger(device, tokens, state)
				.then(this.log)
				.catch(this.error);
		}
	}

	getDeviceType() {
		return this.deviceType ? this.deviceType : false;
	}
};

module.exports = Driver;

'use strict';

const Homey = require('homey');
const request = require('request');
const cookieJar = request.jar();

const tahomaRequest = request.defaults({
	baseUrl: 'https://www.tahomalink.com/enduser-mobile-web/enduserAPI',
	jar: cookieJar
});

let instance = null;

class HttpHelper extends Homey.SimpleClass {

	constructor() {
		if (!instance) {
			super();
			instance = this;
		}

		return instance;
	}

	get(options, callback) {
		var _self = this;

		return new Promise((resolve, reject) => {
			tahomaRequest.get(options, function(error, response, body) {
				if (error) {
					if (callback) {
						callback(error, null);
					}
					reject(error);
					return;
				}

				if (response.statusCode == 401) {
					//no longer authenticated -> login again
					_self.reAuthenticate('get', options, callback);
					return;
				}

				try {
					if (callback) {
						callback(null, JSON.parse(body));
					}
					resolve(JSON.parse(body));
				} catch (e) {
					if (callback) {
						callback(e, null);
					}
					reject(e);
				}
			});
		});
	}

	post(options, callback) {
		var _self = this;

		return new Promise((resolve, reject) => {
			tahomaRequest.post(options, function(error, response, body) {
				if (error) {
					if (callback) {
						callback(error, null);
					}
					reject(error);
					return;
				}

				if (options.uri != '/login' && response.statusCode == 401) {
					//no longer authenticated -> login again
					_self.reAuthenticate('post', options, callback);
					return;
				}

				try {
					var result = (options.json) ? body : JSON.parse(body);
					if (callback) {
						callback(null, result);
					}
					resolve(result);
				} catch (e) {
					if (callback) {
						callback(e, null);
					}
					reject(e);
				}
			});
		});
	}

	delete(options, callback) {
		var _self = this;

		return new Promise((resolve, reject) => {
			tahomaRequest.delete(options, function(error, response, body) {
				if (error) {
					if (callback) {
						callback(error, null);
					}
					reject(error);
					return;
				}

				if (options.uri != '/login' && response.statusCode == 401) {
					//no longer authenticated -> login again
					_self.reAuthenticate('delete', options, callback);
					return;
				}

				if (callback) {
					callback(null, null);
				}
				resolve(null);
			});
		});
	}

	reAuthenticate(forwardMethod, forwardOptions, forwardCallback) {
		var _self = this;

		var username = Homey.ManagerSettings.get('username');
		var password = Homey.ManagerSettings.get('password');
		Tahoma.login(username, password, function(err, result) {
			if (!err && result && result.success && typeof _self[forwardMethod] === 'function') {
				//successfully logged in
				_self[forwardMethod](forwardOptions, forwardCallback);
			}
		});
	}
}

module.exports = new HttpHelper();

